import React from 'react';
import Grid, { ItemProps } from './Grid';

export default {
  title: 'atoms/Grid',
  component: Grid,
};

function Item(props: ItemProps) {
  return (
    <div
      style={{
        width: '100%',
        height: '100%',
        backgroundSize: 'cover',
        backgroundImage: `url(data:image/jpeg;base64,)`,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        color: '#000',
        fontSize: 30,
        fontWeight: 'bolder',
        textShadow: '0 0 4px #fff',
      }}
    >
      {props.index}
    </div>
  );
}

export const Default = () => <Grid itemCount={10} Item={Item} />;
export const OneHundred = () => <Grid itemCount={100} Item={Item} />;
export const TenHundred = () => <Grid itemCount={1000} Item={Item} />;
export const TenThousand = () => <Grid itemCount={10000} Item={Item} />;
export const OneMillion = () => <Grid itemCount={1000000} Item={Item} />;

export const WithHeader = () => (
  <div style={{ width: '100%', padding: '2em', boxSizing: 'border-box' }}>
    <h1>Nice grid</h1>
    <Grid itemCount={100} Item={Item} />
  </div>
);
